import styled from 'styled-components';

export const Title = styled.text`
text-transform:uppercase;
color:#014562;
font-weight:bold;
font-size: 1.5em;
padding-bottom: 2px;
`

export const FinalTitle = styled(Title)`
font-size: 1.2em;
`
